import React, { useEffect, useState } from 'react';
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import playerRankings from '../fixtures/mixProfiles'

import ProfileCard from './ProfileCard'

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));

export default function Profiles() {
    const [searchText, setSearchText] = useState('')
    const classes = useStyles();
    const players = playerRankings.slice(0,20);

    useEffect(()=>{
        const filter = playerRankings.filter((obj)=>{
            if(obj.player.name.toLowerCase().match(searchText.toLowerCase())){
                return true;
            }else {
                return false;
            }
        })
        console.log(filter);
    }, [searchText])

  return (
    <form className={classes.root} noValidate autoComplete="off">
      <Grid container justify="center">
        <Grid item>
          <TextField id="outlined-basic" label="enter profile name" variant="outlined" onChange={(e)=>{setSearchText(e.target.value)}}/>
        </Grid>
        <Grid item>
          <h4>Results: {players.length}</h4>
        </Grid>
      </Grid>
      {players.map((o)=>{
          return <ProfileCard {...o.player}/>
      })}

    </form>
  );
}
