import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import playerRankings from '../fixtures/mixProfiles'


const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));

export default function Profiles() {
    const [searchText, setSearchText] = useState('')
    const classes = useStyles();

    useEffect(()=>{
        const filter = playerRankings.filter((obj)=>{
            if(obj.player.name.toLowerCase().match(searchText.toLowerCase())){
                return true;
            }else {
                return false;
            }
        })
        console.log(filter);
    }, [searchText])

  return (
    <form className={classes.root} noValidate autoComplete="off">
      <TextField id="outlined-basic" label="enter profile name" variant="outlined" onChange={(e)=>{setSearchText(e.target.value)}}/>
    </form>
  );
}
